import React, { useState } from 'react';
import App from './App';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Hamburger from './Hamburger';
import Flip from './Flip';
import Game from './Game';

function Router() {
    return (
        <BrowserRouter className="PlayOptions">
            <Routes>
                <Route path="/" element={<App playerData={playerData} changePlayerData={changePlayerData} />} />
                <Route path="flip" element={<Flip playerData={playerData} changePlayerData={changePlayerData} />} />
                <Route path="create" element={<Navigate to="/" />} />
                <Route path="join" element={<Navigate to="/" />} />
                <Route path="game" element={<Game playerData={playerData} changePlayerData={changePlayerData} />} />
            </Routes>
        </BrowserRouter>
    );
};

export default Router;
