import React, { useState } from 'react';
import App from './App';
import { BrowserRouter, Routes, Route, Redirect} from "react-router-dom";
import Hamburger from './Hamburger';
import Flip from './Flip';
import Game from './Game';

function Router() {
    const [playerData, changePlayerData] = useState({'name': null, 'wallet': null, 'side': null, 'bet': null});

    return (
        <BrowserRouter className="PlayOptions">
            <Routes>
                <Route path="/" element={<App playerData={playerData} changePlayerData={changePlayerData} />} />
                <Route path="flip" element={<Flip playerData={playerData} changePlayerData={changePlayerData} />} />
                <Route path="create" element={<Hamburger />} />
                <Route path="join" element={<Hamburger />} />
                <Route path="game" element={playerData.name !== null ? <Game playerData={playerData} changePlayerData={changePlayerData} /> : <Redirect to="/" />} />
            </Routes>
        </BrowserRouter>
    );
};

export default Router;
