import { useState } from 'react';
import './WaitingPlayers.css';

function Player(props) {
    return (
        <div className='WaitingPlayer'>
            <div className='WaitingPlayerName'>{props.name}</div>
            <div className='WaitingPlayerBet'>{props.bet}</div>
        </div>
    )
}

async function loadWaitingPlayers() {
    fetch('/findWaiting')
        .then(res => res.json())
        .then((result) => {
            let players = []
            for (let player in result.players) {
                players.push(<Player name={player.name} bet={player.bet} />);
            }
            return players;
        });
}

function WaitingPlayers() {
    const [WaitingPlayers, setWaitingPlayers] = useState([]);

    return (
        <div className="WaitingPlayers">
            <button onClick={() => {let players = loadWaitingPlayers(); console.log(players); setWaitingPlayers(players);}}>reload</button>
            <div className='WaitingPlayersTitle'>All Players</div>
            <div className='WaitingPlayersBody'>{WaitingPlayers}</div>
        </div>
    );
};

export default WaitingPlayers;
