import { useState, useEffect } from "react";
import OverlayMenu from "./OverlayMenu";
import PlayOptions from "./PlayOptions";
import Header from "./Header";
import WaitingPlayers from "./WaitingPlayers";
import './App.css';

function App() {
    const [isMenuActive, activeMenu] = useState(false);
    const [resp, setResp] = useState(null);
    const [balance, setBalance] = useState(JSON.parse(localStorage.getItem('balance')) !== null ? parseInt(JSON.parse(localStorage.getItem('balance'))) : 100);
    console.log(balance)

    localStorage.setItem('balance', JSON.stringify(balance))

    if (resp)
        console.log(resp.publicKey.toString());

    return (
    <div className="app">
        <Header onMenuClick={() => activeMenu(!isMenuActive)} resp={resp} balance={balance} setResp={setResp}/>
        <div className='AppBody'>
            <WaitingPlayers className="WaitingPlayers"/>
            <PlayOptions setBalance={setBalance}/>
            <div className="AppEmptyDiv"></div>
        </div>
        <OverlayMenu 
            isMenuActive={isMenuActive}
            closeMenu={() => activeMenu(false)} 
            />
    </div>
    )
}

export default App;
