import CoinLogo from './CoinLogo';
import Hamburger from './Hamburger'
import './Header.css'

function Header(props) {
    const connectWallet = async (setResp) => {
        const isPhantomInstalled = window.solana && window.solana.isPhantom
        const isMetamaskInstalled = window.ethereum
        if (isPhantomInstalled)
            connectToPhantom(setResp);
        else if (isMetamaskInstalled)
            connectToMetamask(setResp);
        else {
            alert("only Phantom and Metamask wallets are currently supported");
        }
    }

    const connectToPhantom = async (setResp) => {
        try {
            const resp = await window.solana.connect();
            resp.publicKey.toString()
            setResp({'phantom': resp, 'metamask': null});
            // 26qv4GCcx98RihuK3c4T6ozB3J7L6VwCuFVc7Ta2A3Uo 
        } catch (err) {
            console.log("rejected");
            // { code: 4001, message: 'User rejected the request.' }
        }
    }

    const connectToMetamask = async (setResp) => {
        try {
            const accounts = await ethereum.request({ method: 'eth_requestAccounts' });
            const account = accounts[0];
            console.log(accounts);
        } catch (err) {
            console.log("rejected");
        }
    }

    return (
        <div className='StartHeader'>
            <button className="ConnectWallet" onClick={() => {connectWallet(props.setResp)}}>Connect Wallet</button>
            <a id="StartLogo" href='/'>
                <div className='StartLogo'>
                    <CoinLogo width="68" height="68" class="CoinLogo"/>
                    <span id="flip">FLIP</span>
                </div>
            </a>
            <Hamburger onMenuClick={props.onMenuClick} width="50" height="50" class="Hamburger"/>
        </div>  

    );
};

export default Header;
